<node ID="AF817AFA-3369-4B9B-A882-D540A4DD75E4" TEXT="call &#32487;&#25215;&#65288;&#20511;&#29992;&#26500;&#36896;&#20989;&#25968;&#65289;" COLOR="#344853" POSITION="right" STYLE="fork"><edge COLOR="#25BAD1" WIDTH="6" /><font NAME="HelveticaNeue" SIZE="18" /><richcontent TYPE="NOTE"><html><head></head>
<body><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>function Parent() {</p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>	this.x = 100;</p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>	this.y = 199;</p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>}</p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>Parent.prototype.fn = function() {}</p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '> </p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>function Child() {</p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>	this.d = 100;</p><p><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>	Parent.call(this); //</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "PingFang SC"; -cocoa-font-postscriptname: "PingFangSC-Regular"; '>构造函数中的</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>this</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "PingFang SC"; -cocoa-font-postscriptname: "PingFangSC-Regular"; '>就是当前实例</span></p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>}</p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>var p = new Parent();</p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>var c = new Child();</p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>console.log(p)  //Parent {x: 100, y: 199}</p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>console.log(c)  //Child {d: 100, x: 100, y: 199}</p></body>
</html>
</richcontent>
