<node ID="9D130CE6-99C5-48B9-A50F-AF4D99CBF24E" TEXT="&#23492;&#29983;&#32452;&#21512;&#24335;&#32487;&#25215;" COLOR="#344853" POSITION="right" STYLE="fork"><edge COLOR="#922AA4" WIDTH="6" /><font NAME="PingFangSC-Regular" SIZE="18" /><richcontent TYPE="NOTE"><html><head></head>
<body><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>function inheritPrototype(subType,superType){</p><p><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>	var prototype = Object.create(superType.prototype); //</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "PingFang SC"; -cocoa-font-postscriptname: "PingFangSC-Regular"; '>创建对象</span></p><p><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>	prototype.constructor = subType; //</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "PingFang SC"; -cocoa-font-postscriptname: "PingFangSC-Regular"; '>增强对象</span></p><p><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>	subType.prototype = prototype; //</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "PingFang SC"; -cocoa-font-postscriptname: "PingFangSC-Regular"; '>指定对象</span></p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>}</p><p></p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "PingFang SC"; -cocoa-font-postscriptname: "PingFangSC-Regular"; '>完整：</p><p></p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>function F(){</p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>	this.x=100;</p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>}</p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>F.prototype.showX=function(){};</p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>function S(){</p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>	this.y = 200</p><p><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>	F.call(this)//</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "PingFang SC"; -cocoa-font-postscriptname: "PingFangSC-Regular"; '>只继承了私有的；</span></p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>}</p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>function inheritPrototype(subType,superType){</p><p><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>	var prototype = Object.create(superType.prototype);//</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "PingFang SC"; -cocoa-font-postscriptname: "PingFangSC-Regular"; '>创建对象</span></p><p><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>	prototype.constructor = subType;//</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "PingFang SC"; -cocoa-font-postscriptname: "PingFangSC-Regular"; '>增强对象</span></p><p><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>	subType.prototype = prototype;//</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "PingFang SC"; -cocoa-font-postscriptname: "PingFangSC-Regular"; '>指定对象</span></p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>}</p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>inheritPrototype(S,F)</p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>var p1=new S;</p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>console.dir(p1)</p></body>
</html>
