<body><p><span style='color: rgba(14, 114, 163, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>Object</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>.prototype.objCustom = </span><span style='color: rgba(38, 38, 38, 1.000000); font: bold 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Bold"; '>function</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '> () {}; </span></p><p><span style='color: rgba(14, 114, 163, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>Array</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>.prototype.arrCustom = </span><span style='color: rgba(38, 38, 38, 1.000000); font: bold 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Bold"; '>function</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '> () {};</span></p><p></p><p><span style='color: rgba(38, 38, 38, 1.000000); font: bold 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Bold"; '>let</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '> iterable = [</span><span style='color: rgba(14, 110, 108, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>3</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>, </span><span style='color: rgba(14, 110, 108, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>5</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>, </span><span style='color: rgba(14, 110, 108, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>7</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>];</span></p><p><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>iterable.foo = </span><span style='color: rgba(209, 0, 52, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>&quot;hello&quot;</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>;</span></p><p></p><p><span style='color: rgba(38, 38, 38, 1.000000); font: bold 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Bold"; '>for</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '> (</span><span style='color: rgba(38, 38, 38, 1.000000); font: bold 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Bold"; '>let</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '> i </span><span style='color: rgba(38, 38, 38, 1.000000); font: bold 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Bold"; '>in</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '> iterable) {</span></p><p><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>  </span><span style='color: rgba(14, 114, 163, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>console</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>.log(i); </span><span style='color: rgba(135, 135, 117, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>// logs 0, 1, 2, &quot;foo&quot;, &quot;arrCustom&quot;, &quot;objCustom&quot;</span></p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>}</p><p></p><p><span style='color: rgba(38, 38, 38, 1.000000); font: bold 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Bold"; '>for</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '> (</span><span style='color: rgba(38, 38, 38, 1.000000); font: bold 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Bold"; '>let</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '> i </span><span style='color: rgba(38, 38, 38, 1.000000); font: bold 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Bold"; '>of</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '> iterable) {</span></p><p><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>  </span><span style='color: rgba(14, 114, 163, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>console</span><span style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>.log(i); </span><span style='color: rgba(135, 135, 117, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>// logs 3, 5, 7</span></p><p style='color: rgba(38, 38, 38, 1.000000); font: 11px "Menlo"; -cocoa-font-postscriptname: "Menlo-Regular"; '>}</p></body>
